import '../styles/Carousel.css';
import fogoEmoji from '../assets/fogo-emoji.png';

function Carousel() {
    return (
        <>
            <main className='carousel-container'>
                <div className='carousel-wrapper'>
                    <div className='carousel-content'>
                        <div className='carousel-texts'>
                            <h2 className='carousel-subtitle'>Melhores ofertas personalizadas</h2>
                            
                            <h1 className='carousel-title'>Queima de estoque Nike
                              <img className='carousel-fire-icon' src={fogoEmoji} alt="Fogo Emoji"/>
                            </h1>

                            <p className='carousel-description'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Perspiciatis maxime officiis excepturi voluptatem dolor facere.</p>

                            <a className='carousel-button' href="">Ver Ofertas</a>
                        </div>
                        <img className='carousel-image'
                            src={TenisNike}
                            alt='TÃªnis Nike'
                        />
                    </div>
                </div>
            </main>
        </>
    );
}

export default Carousel;
