import React, { useEffect, useRef, useState } from "react";
import "../styles/HomePage.css";
import tennisImage from "../assets/nike-shoe-frontpage.png";

export default function ImageCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const slidesRef = useRef(null);
  const totalSlides = 4;

  useEffect(() => {
    const autoSlideInterval = setInterval(showNextSlide, 3000);
    return () => clearInterval(autoSlideInterval);
  }, [currentIndex]);

  const updateSlideIndex = (index) => {
    if (index >= totalSlides) {
      setCurrentIndex(0);
    } else if (index < 0) {
      setCurrentIndex(totalSlides - 1);
    } else {
      setCurrentIndex(index);
    }
  };

  const showNextSlide = () => {
    updateSlideIndex(currentIndex + 1);
  };

  const showPreviousSlide = () => {
    updateSlideIndex(currentIndex - 1);
  };

  const goToSlide = (index) => {
    updateSlideIndex(index);
  };

  useEffect(() => {
    if (slidesRef.current) {
      slidesRef.current.style.transform = `translateX(${-currentIndex * 100}%)`;
      const bubbles = document.querySelectorAll(".carousel-bubble");
      bubbles.forEach((bubble, idx) => {
        bubble.classList.toggle("active", idx === currentIndex);
      });
    }
  }, [currentIndex]);

  return (
    <div>
      <div className="carousel">
        <div className="carousel-inner" ref={slidesRef}>
          {[...Array(totalSlides)].map((_, idx) => (
            <div className="carousel-item" key={idx}>
              <div className="carousel-content">
                <div className="carousel-text">
                  <div className="text-promotion">
                    <p>Melhores ofertas personalizadas</p>
                  </div>
                  <div className="title-promotion">
                    <h1>Queima de estoque Nike ðŸ”¥</h1>
                  </div>
                  <div className="description-promotion">
                    <p>
                      Consequat culpa exercitation mollit nisi excepteur do do
                      tempor laboris eiusmod irure consectetur.
                    </p>
                  </div>
                  <div className="button-promotion">
                    <button className="offer-button">Ver ofertas</button>
                  </div>
                </div>
                <div className="carousel-image">
                  <img src={tennisImage} alt="Tenis Carrosel" />
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="carousel-bubbles">
          {[...Array(totalSlides)].map((_, idx) => (
            <span
              className={`carousel-bubble ${idx === currentIndex ? "active" : ""}`}
              onClick={() => goToSlide(idx)}
              key={idx}
            ></span>
          ))}
        </div>
      </div>
    </div>
  );
}
