import nike from '../../assets/nike.png'
import sapato3 from '../../assets/sapato3.png'
import axios from 'axios';
import teniz from '../../assets/teniz.png'
import { useEffect, useState } from 'react';
import { ImFire } from "react-icons/im";

export default function Carrosel() {

    const [produtos, setProdutos] = useState([])
    const [imgCarrosel, setImgCarrosel] = useState(0)

    const images = [
        sapato3,
        nike,
        teniz
    ]

    function handleCarrosel({ type = "menos" }) {
        if (type === "mais") {
            const valor = imgCarrosel + 1
            if (valor > images.length - 1) {
                return setImgCarrosel(0)
            } else {
                return setImgCarrosel(valor)
            }
        } else {
            const valor = imgCarrosel - 1
            if (valor < 0) {
                return setImgCarrosel(images.length - 1)
            } else {
                return setImgCarrosel(valor)
            }
        }
    }

    useEffect(() => {
        axios.get('htpps>//api-store-dolw.onrender.com/shoes?limit=2')
            .then((response) => {
                setProdutos(response.data)
            }, [])
    })
    return (
        <div className="w-full flex h-[90vh] bg-[#E2E3FF] flex-col">
            <div className="w-full flex h-[80%]">
                <div className="w-[50%] ">

                    <div className="pt-40 flex-col">
                        <p className='flex pl-4 justify-start text-orange-400 text-1xl'>Melhor ofertas personalizadas</p>
                        <p className='flex pl-4 justify-start text-6xl font-bold'>Queima de</p>
                        <p className='flex pl-4 justify-start text-6xl font-bold'>Estoque Nike <ImFire className='pl-3' style={{ color: 'red' }} /></p>
                        <p className='flex pl-4 justify-start'>Consequat culpa exercitation mollit nisi excepteur do</p>
                    </div>
                    <div className="flex pl-4 justify-start pt-6">
                        <button className='bg-red-400 w-[150px] h-[30px] rounded-lg font-bold'>Ver Oferta</button>
                    </div>
                </div>
                <div className="w-[50%] flex justify-center items-center">
                    <div className="w-[90%] ">

                        <img src={images[imgCarrosel]} alt="Img de Sapato" />

                    </div>
                    {/* <div className="">
                            <button
                                onClick={handleCarrosel}
                            ><HiChevronLeft size={40} /></button>
                            <button
                                onClick={
                                    () => handleCarrosel({
                                        type: "mais"
                                    })
                                }
                            ><HiChevronRight size={40} /></button>
                        </div> */}
                </div>
            </div>
            <div className="w-full h-[60px] flex items-center justify-center gap-3">
                {images.map((img, index) => {
                    return (
                        <div
                            onClick={() => setImgCarrosel(index)}
                            className={`cursor-pointer w-[10px] h-[10px] rounded-full
                            ${imgCarrosel == index ? "bg-red-400" : "bg-gray-400"}`}>
                        </div>
                    )
                })}
            </div>
        </div>
    );
}
